import React, { useCallback, useState } from 'react';
import './App.css';

function App() {

  //Declare react hooks to dynamically render response from REST API and handles changes to input
  const[apiResponse, setApiResponse] = useState('No Request Has Been Sent!');
  const[paramString, setParamString] = useState('');

  //Arrow function that changes the paramString hook as the param is typed
  const handleChange = (e) =>{
    console.log(e.target.value);
  }
  return (
    <div className="body">


      <nav className="navbar navbar-dark bg-dark navbar-expand-lg mb-5">
        <a className="navbar-brand" href="#">Veer AI Assesment</a>
        <div className="collapse navbar-collapse">
          <div className="navbar-nav">
            <a className="nav-item nav-link">Contact Page</a>
            <a className="nav-item nav-link">AJAX Button API</a>
          </div>
      </div>
      </nav>


      <div className="container text-center">


        <h1 className="display-4 mb-5" id="home-title">Type A Parameter Below to Test The REST API</h1>
        <br/>
        <br/>
        <div className="card bg-dark">
          <div className="card-body text-center justify-content-center mt-3 mb-3">
            <form id="param-form">
              <div className="form-group">
                <label for="param-input" id="input-label">Param String</label>
                <input id="param-input" className="form-control mt-2" type="text" placeholder="e.g. Apple, Car, ABC, etc." onChange={e => handleChange(e)}></input>
              </div>
              <button className="btn btn-success mt-2">Submit Request!</button>
            </form>
          </div>
        </div>
        <br/>
        <br/>


        <p className="lead mt-5" id="response-title">Response From REST API</p>
        <div>
          <h3>{apiResponse}</h3>
        </div>

      </div>
    </div>
  );
}

export default App;
